import React from 'react';

const Question = ({ question, handleAnswerChange }) => {
  switch (question.type) {
    case 'radio':
      return (
        <div>
          <label>{question.question}</label>
          {question.options.map((option, i) => (
            <div key={i}>
              <input
                type="radio"
                name={question.id}
                value={option}
                onChange={handleAnswerChange}
              />
              {option}
            </div>
          ))}
        </div>
      );
    case 'checkbox':
      return (
        <div>
          <label>{question.question}</label>
          {question.options.map((option, i) => (
            <div key={i}>
              <input
                type="checkbox"
                name={`${question.id}_${i}`}
                value={option}
                onChange={handleAnswerChange}
              />
              {option}
            </div>
          ))}
        </div>
      );
    case 'text':
      return (
        <div>
          <label>{question.question}</label>
          <input
            type="text"
            name={question.id}
            onChange={handleAnswerChange}
          />
        </div>
      );
    default:
      return null;
  }
};

export default Question;
