import React from 'react';
import { useDrag, useDrop } from 'react-dnd';

const Blog = ({ blog, index, moveBlog }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: 'BLOG',
    item: { index },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }), [index]);

  const [, drop] = useDrop(() => ({
    accept: 'BLOG',
    hover: (item) => {
      if (item.index !== index) {
        moveBlog(item.index, index);
        item.index = index;
      }
    },
  }), [index, moveBlog]);

  return (
    <div ref={(node) => drag(drop(node))} style={{ opacity: isDragging ? 0.5 : 1 }}>
      <h3>{blog.title}</h3>
      <p>{blog.content}</p>
    </div>
  );
};

export default Blog;
