import React, { useState } from 'react';
import './App.css';

function App() {
  const initialQuestions = [
    {
      id: 1,
      question: "Question 1",
      options: ["Option 1", "Option 2", "Option 3"],
      selectedOption: "",
      checkboxChecked: false,
      textValue: ""
    },
    {
      id: 2,
      question: "Question 2",
      options: ["Option A", "Option B", "Option C"],
      selectedOption: "",
      checkboxChecked: false,
      textValue: ""
    }
  ];

  const [questions, setQuestions] = useState(initialQuestions);
  const [draggedItem, setDraggedItem] = useState(null);

  const onDragStart = (e, index) => {
    setDraggedItem(questions[index]);
    e.dataTransfer.effectAllowed = "move";
  };

  const onDragOver = (e) => {
    e.preventDefault();
  };

  const onDrop = (e, index) => {
    e.preventDefault();
    const updatedQuestions = questions.filter((item) => item !== draggedItem);
    updatedQuestions.splice(index, 0, draggedItem);

    const reorderedQuestions = updatedQuestions.map((item, idx) => ({
      ...item,
      id: idx + 1
    }));

    setQuestions(reorderedQuestions);
    setDraggedItem(null);
  };

  const handleOptionChange = (questionIndex, optionIndex) => {
    const updatedQuestions = [...questions];
    updatedQuestions[questionIndex].selectedOption =
      updatedQuestions[questionIndex].options[optionIndex];
    setQuestions(updatedQuestions);
  };

  const handleCheckboxChange = (questionIndex) => {
    const updatedQuestions = [...questions];
    updatedQuestions[questionIndex].checkboxChecked = !updatedQuestions[
      questionIndex
    ].checkboxChecked;
    setQuestions(updatedQuestions);
  };

  const handleTextChange = (questionIndex, e) => {
    const updatedQuestions = [...questions];
    updatedQuestions[questionIndex].textValue = e.target.value;
    setQuestions(updatedQuestions);
  };

  return (
    <div className="App">
      {questions.map((item, index) => (
        <div
          key={item.id}
          className="draggable"
          draggable
          onDragStart={(e) => onDragStart(e, index)}
          onDragOver={onDragOver}
          onDrop={(e) => onDrop(e, index)}
        >
          <div className="drag-handle">â‰¡</div>
          <div className="id">ID: {item.id}</div>
          <div className="question">{item.question}</div>
          <div className="options">
            {item.options.map((option, optionIndex) => (
              <div key={optionIndex}>
                <input
                  type={item.checkboxChecked ? "checkbox" : "radio"}
                  id={`option_${index}_${optionIndex}`}
                  name={`question_${index}`}
                  value={option}
                  checked={
                    item.selectedOption === option ||
                    (item.checkboxChecked && item.selectedOption.includes(option))
                  }
                  onChange={() => handleOptionChange(index, optionIndex)}
                />
                <label htmlFor={`option_${index}_${optionIndex}`}>{option}</label>
              </div>
            ))}
          </div>
          <div>
            <input
              type="checkbox"
              id={`checkbox_${index}`}
              checked={item.checkboxChecked}
              onChange={() => handleCheckboxChange(index)}
            />
            <label htmlFor={`checkbox_${index}`}>Check me</label>
          </div>
          <div>
            <input
              type="text"
              value={item.textValue}
              onChange={(e) => handleTextChange(index, e)}
            />
          </div>
        </div>
      ))}
    </div>
  );
}

export default App;
